import React, { useState } from 'react';
import axios from 'axios';
import ResultCard from './ResultCard';

function VolumeForm() {
  const [containerVolume, setContainerVolume] = useState('');
  const [containerImage, setContainerImage] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const calculateVolume = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    setLoading(true);

    if (!containerVolume || !containerImage) {
      setError('⚠️ Lütfen tüm alanları doldurun!');
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append('containerVolume', containerVolume);
    formData.append('containerImage', containerImage);

    try {
      const response = await axios.post('http://127.0.0.1:5000/calculate', formData);
      setResult(response.data);
    } catch (err) {
      setError('❌ API hatası: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 bg-gray-800 text-white rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold mb-4">Konteyner Hacim Hesaplama</h1>
      <form onSubmit={calculateVolume}>
        <label>Konteyner Hacmi (m³):</label>
        <input
          type="number"
          value={containerVolume}
          onChange={(e) => setContainerVolume(e.target.value)}
          className="w-full p-2 bg-gray-700 rounded"
          required
        />

        <label>Konteyner Fotoğrafı:</label>
        <input
          type="file"
          onChange={(e) => setContainerImage(e.target.files[0])}
          className="w-full p-2 bg-gray-700 rounded"
          required
        />

        <button
          type="submit"
          className="bg-yellow-400 text-black p-2 mt-4 rounded hover:bg-yellow-500 transition"
        >
          {loading ? 'Hesaplanıyor...' : 'Hesapla'}
        </button>
      </form>

      {error && <div className="mt-4 text-red-500">{error}</div>}
      {result && (
        <ResultCard
          fillPercentage={result.fill_percentage}
          filledVolume={result.filled_volume}
        />
      )}
    </div>
  );
}

export default VolumeForm;
